parameters.jsCode:
const allInputItems = $input.all();

console.log(`Received ${allInputItems.length} items to process.`);

if (allInputItems.length > 0) {
    console.log("Structure of the first input item's JSON:", JSON.stringify(allInputItems[0].json, null, 2));
}

let collectedNodes = [];
let collectedConnections = {};

for (const item of allInputItems) {

    const nodesFromItem = item.json?.x?.v;
    if (nodesFromItem) {
        if (Array.isArray(nodesFromItem)) {
            collectedNodes = collectedNodes.concat(nodesFromItem);
             console.log(`Added ${nodesFromItem.length} nodes from item.`);
        } else if (typeof nodesFromItem === 'object') {
            collectedNodes.push(nodesFromItem);
            console.log(`Added 1 node from item: ${nodesFromItem.name || nodesFromItem.id}`);
        }
    } else {
        console.log("WARN: No node data found at 'item.json.x.v' for an item:", JSON.stringify(item.json));
    }

    const connectionsFromItem = item.json?.x?.connection;
    if (connectionsFromItem && typeof connectionsFromItem === 'object') {
        Object.assign(collectedConnections, connectionsFromItem);
        console.log("Merged connections from item:", JSON.stringify(connectionsFromItem));
    } else {
         console.log("WARN: No connection data found at 'item.json.x.connection' for an item:", JSON.stringify(item.json));
    }
}

console.log(`Total collected dynamic nodes: ${collectedNodes.length}`);
console.log("Total collected dynamic connections:", JSON.stringify(collectedConnections, null, 2));


const tools = {
  name: "Mcp-Fabric",
  nodes: [
    {
      parameters: { path: "8d9e3a7a-59f8-418a-b21c-ffe227735ff4" },
      type: "@n8n/n8n-nodes-langchain.mcpTrigger",
      typeVersion: 1,
      position: [0, 0],
      id: "04274350-81af-4cf8-99ae-c9e29a451dec",
      name: "MCP Server Trigger",
      webhookId: "8d9e3a7a-59f8-418a-b21c-ffe227735ff4"
    },
    ...collectedNodes
  ],
  pinData: {},
  connections: {
    "": {
      ai_tool: [[{ node: "MCP Server Trigger", type: "ai_tool", index: 0 }]]
    },

    ...collectedConnections
  },
  active: false,
  settings: { executionOrder: "v1" },
  versionId: "0488af3f-de3c-41ef-96b5-cc9957d3238b",
  meta: { instanceId: "558d88703fb65b2d0e44613bc35916258b0f0bf983c5d4730c00c424b77ca36a" },
  id: "qVlrn9OUVb9mDhMH",
  tags: []
};

console.log("Final constructed 'tools' object:", JSON.stringify(tools, null, 2));


return [ { json: tools } ];

